def mapIdToPath(id):
    """
    NOTE: input id starts from 0 not 1, HPC job id starts from 1
    so should be mapIdToPath(JOBID-1)

    we run 45 jobs/tasks (allocate 13 gpus) at one time as each scene has 45 clips
    so we run 10 times as there are in total 10 scenes
    for each task id, we run videos for 1 scene, 1 seg, 1 speed, i.e. for loop 5 * 10 = 50 videos
    e.g. sbatch --array=1-10:1 -A STARS-SL3-GPU submission_script

    id is from 0-44, map id -> path, seg, speed

    e.g. id 0 -> paths[0], segs[0], speeds[0]
         id 1 -> paths[0], segs[0], speeds[1]
    Note that id in HPC starts from 1, so delete id by 1 when call the function
    mapIdToPath(0) -> (1, 1, 1) path1 seg1 speed1
    mapIdToPath(16) -> (2, 3, 2) path2 seg3 speed2
    """
    pathIdx = int(floor(id/9))
    segIdx = int(floor((id - pathIdx * 9) / 3))
    speedIdx = (id - pathIdx * 9) % 3
    paths = [1, 2, 3, 4, 5]
    segs = [1, 2, 3,]
    speeds = [1, 2, 3,]
#    print(f'pathIdx {pathIdx}, segIdx {segIdx} speedIdx {speedIdx}')
    return paths[pathIdx], segs[segIdx], speeds[speedIdx]



def create_df_within_range(all_scenes):
    all_scenes_dfs_by_bitrate = []
    bitrate_map = {0:1000, 1:1500, 2:2000, 3:3000, 4:4000}
    for scene in all_scenes:
        variable_name = f"{scene}_within_JOD_range"
        fps_list = []
        resolution_list = []
        bitrate_list = []
        path_list = []
        velocity_list = []
        if variable_name in globals():
            comb_within_range = globals()[variable_name]
            print(f'comb_within_range {comb_within_range}')
            print(f'variable_name {comb_within_range}')
            for path, sheet_val in comb_within_range.items(): # sheet is like path1_seg1_1
                    for idx, comb_per_bitrate in enumerate(sheet_val):
                        for (fps, resolution) in comb_per_bitrate:
                            fps_list.append(fps)
                            resolution_list.append(resolution)
                            # bitrate_list.append((idx + 1) * 500)  # Assuming velocity is the index+1
                            bitrate_list.append(bitrate_map[idx])
                            path_list.append(f'{scene}')
                            velocity = int(path.split('_')[-1])
                            # velocity = 1
                            velocity_list.append(velocity)
        else:
            print(f"Variable {variable_name} does not exist.\n")
        data = {
            'resolution': resolution_list,
            'fps': fps_list,
            'bitrate': bitrate_list,
            'path': path_list,
            'velocity': velocity_list
        }

        df = pd.DataFrame(data)
        bitrate_groups = df.groupby('bitrate')
        dfs_by_bitrate = {bitrate: group_df for bitrate, group_df in bitrate_groups}
        # with open(f'reference_velocity/{scene}_cleaned.txt', 'r') as file:
        #     lines = file.readlines()
        #     velocities = [float(line.strip()) for line in lines]
        #     velocities = np.array(velocities)
        #     velocities /= 51
        #     frame_limit = frame_per_fps_video(166)
        #     velocities /= (frame_limit + 1)
        #     # print(f'velocities {velocities}')

            # for bitrate, group_df in dfs_by_bitrate.items():
            #     group_df['velocity'] = velocities
            #     # group_df = group_df.drop(columns=['bitrate'])
                # dfs_by_bitrate[bitrate] = group_df  # Update the dictionary with the new DataFrame
        all_scenes_dfs_by_bitrate.append(dfs_by_bitrate)
    
    combined_dfs_by_bitrate = {}  
    # Loop through each scene's dfs_by_bitrate
    for scene_dfs in all_scenes_dfs_by_bitrate:
        for bitrate, df in scene_dfs.items():
            if bitrate not in combined_dfs_by_bitrate:
                # If bitrate not present, create a new entry
                combined_dfs_by_bitrate[bitrate] = df
            else:
                # If bitrate exists, concatenate the DataFrames
                combined_dfs_by_bitrate[bitrate] = pd.concat([combined_dfs_by_bitrate[bitrate], df])
    # print(f'combined_dfs_by_bitrate {combined_dfs_by_bitrate}')
    return combined_dfs_by_bitrate
